function checkForNotifications(){$.getJSON("https://raw.githubusercontent.com/Christianjuth/calculator-browser-extension/JSON/notifications.json",function(e){for(e.sort(compare),i=0;i<e.length;i++){var o=e[i],t=$.parseJSON(localStorage.notify),n=parseInt(moment(t.date,"YYYYMMDD").add("days",t.delay).format("YYYYMMDD"))<=parseInt(moment().format("YYYYMMDD")),r=o.id==parseInt($.parseJSON(localStorage.notify).id)+1;if(o.id>$.parseJSON(localStorage.notify).id){if("true"==localStorage.noNotifyDelay&&"false"==localStorage.limitNotifications&&r){createNotification(o.title,o.message),t.id=o.id,t.date=moment().format("YYYYMMDD"),t.delay=Math.floor(4*Math.random()+3),localStorage.notify=JSON.stringify(t);break}if(n&&"false"==localStorage.limitNotifications){createNotification(o.title,o.message),t.id=o.id,t.date=moment().format("YYYYMMDD"),t.delay=Math.floor(4*Math.random()+3),localStorage.notify=JSON.stringify(t);break}if("true"==localStorage.limitNotifications&&1==o.important&&n){createNotification(o.title,o.message),t.id=o.id,t.date=moment().format("YYYYMMDD"),t.delay=Math.floor(4*Math.random()+3),localStorage.notify=JSON.stringify(t);break}}}setTimeout(function(){checkForNotifications()},3e5)})}function createNotification(e,o){var t={type:"basic",title:e,message:o,iconUrl:"../resources/icons/icon.png"};chrome.notifications.create("",t,function(){})}function compare(e,o){return e.id<o.id?-1:e.id>o.id?1:0}$(document).ready(function(){checkForNotifications()});